CREATE TABLE IF NOT EXISTS `aiml` ( `id` int(11) NOT NULL AUTO_INCREMENT, `bot_id` int(11) NOT NULL DEFAULT '1', `pattern` varchar(256) NOT NULL, `thatpattern` varchar(256) NOT NULL, `template` text NOT NULL, `topic` varchar(256) NOT NULL, `filename` varchar(256) NOT NULL, PRIMARY KEY (`id`), UNIQUE KEY `id` (`id`), KEY `topic` (`topic`), KEY `thatpattern` (`thatpattern`), KEY `pattern` (`pattern`), KEY `bot_id` (`bot_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `aiml_userdefined` ( `id` int(11) NOT NULL AUTO_INCREMENT, `pattern` varchar(256) NOT NULL, `thatpattern` varchar(256) NOT NULL, `template` text NOT NULL, `user_id` varchar(256) NOT NULL, `bot_id` int(11) NOT NULL, `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`), UNIQUE KEY `id` (`id`), KEY `pattern` (`pattern`), KEY `thatpattern` (`thatpattern`), KEY `bot_id` (`bot_id`), KEY `user_id` (`user_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `botpersonality` ( `id` int(11) NOT NULL AUTO_INCREMENT, `bot_id` tinyint(4) NOT NULL DEFAULT '0', `name` varchar(256) NOT NULL DEFAULT '', `value` text NOT NULL, PRIMARY KEY (`id`), KEY `botname` (`bot_id`,`name`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `bots` ( `bot_id` int(11) NOT NULL AUTO_INCREMENT, `bot_name` varchar(256) NOT NULL, `bot_desc` varchar(256) NOT NULL, `bot_active` int(11) NOT NULL DEFAULT '1', `bot_parent_id` int(11) NOT NULL DEFAULT '0', `format` varchar(10) NOT NULL DEFAULT 'html', `save_state` enum('session','database') NOT NULL DEFAULT 'session', `conversation_lines` int(11) NOT NULL DEFAULT '7', `remember_up_to` int(11) NOT NULL DEFAULT '10', `debugemail` text NOT NULL, `debugshow` int(11) NOT NULL DEFAULT '1', `debugmode` int(11) NOT NULL DEFAULT '1', `error_response` text NOT NULL, `default_aiml_pattern` varchar(256) NOT NULL DEFAULT 'RANDOM PICKUP LINE', `unknown_user` varchar(256) NOT NULL DEFAULT 'Seeker', PRIMARY KEY (`bot_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `client_properties` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_id` int(11) NOT NULL, `bot_id` int(11) NOT NULL, `name` text NOT NULL, `value` text NOT NULL, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `conversation_log` ( `id` int(11) NOT NULL AUTO_INCREMENT, `input` text NOT NULL, `response` longtext NOT NULL, `user_id` int(11) NOT NULL, `convo_id` text NOT NULL, `bot_id` int(11) NOT NULL, `timestamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `myprogramo` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_name` varchar(256) NOT NULL, `password` varchar(256) NOT NULL, `last_ip` varchar(25) NOT NULL, `last_login` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`), UNIQUE KEY `user_name` (`user_name`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `spellcheck` ( `id` int(11) NOT NULL AUTO_INCREMENT, `missspelling` varchar(100) NOT NULL, `correction` varchar(100) NOT NULL, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `srai_lookup` ( `id` int(11) NOT NULL AUTO_INCREMENT, `bot_id` int(11) NOT NULL, `pattern` text NOT NULL, `template_id` int(11) NOT NULL, PRIMARY KEY (`id`), KEY `template_id` (`template_id`), KEY `pattern` (`bot_id`,`pattern`(64))) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Contains previously stored SRAI calls';
CREATE TABLE IF NOT EXISTS `undefined_defaults` ( `id` int(11) NOT NULL AUTO_INCREMENT, `bot_id` int(11) NOT NULL, `user_id` int(11) NOT NULL DEFAULT '0', `pattern` text NOT NULL, `template` varchar(256) NOT NULL, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `unknown_inputs` ( `id` int(11) NOT NULL AUTO_INCREMENT, `bot_id` int(11) NOT NULL, `input` text NOT NULL, `user_id` int(11) NOT NULL, `timestamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `users` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_name` text NOT NULL, `session_id` varchar(256) NOT NULL, `bot_id` int(11) NOT NULL, `chatlines` int(11) NOT NULL, `ip` varchar(100) NOT NULL, `referer` text NOT NULL, `browser` text NOT NULL, `date_logged_on` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `last_update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `state` text NOT NULL, PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `wordcensor` ( `censor_id` int(11) NOT NULL AUTO_INCREMENT, `word_to_censor` varchar(50) NOT NULL, `replace_with` varchar(50) NOT NULL DEFAULT '****', `bot_exclude` varchar(256) NOT NULL, PRIMARY KEY (`censor_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;